package Feb_21_2022;

public class Bitwise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Compute the Output for a=7 & b=5 ");
		int a = 7;
		int b = 5;
		//and
		System.out.println(a&b);
		//or
		System.out.println(a|b);
		//XOR
		System.out.println(a^b);
		
		
		
		System.out.println("Compute the Output for c=17 & b=9 ");

		int c = 17;
		int d =9;
		//and
		System.out.println(c&d);
		//or
		System.out.println(c|d);
		//XOR
		System.out.println(c^d);
		
		
		System.out.println("Compute the Output for a1=91 & b1=56 ");

		int a1 = 91;
		int b1 = 56;
		//and
		System.out.println(a1&b1);
		//or
		System.out.println(a1|b1);
		//XOR
		System.out.println(a1^b1);







	}

}
